template:
using System;
namespace ex251027
{
    class Point : ICloneable
    {
        private int x;
        public int X { get { return x; } set { x = value; } }
        private int y;
        public int Y { get { return y; } set { y = value; } }

        public Point(int x, int y)
        {
            /* 빈칸 */
            this.y = y;
        }

        public override string ToString()
        {
            return $"({X},{Y})";
        }

        public object Clone()
        {
            return MemberwiseClone();
        }
    }
    class Rect : ICloneable
    {
        private Point pt;
        public Point PT { get { return pt; } }
        private int width, height;
        public int Width { get { return width; } set { width = value; } }
        public Rect(Point pt, int width, int height)
        {
            this.pt = pt;
            this.width = width;
            this.height = height;
        }

        public object Clone()
        {
            Rect cpRect = (Rect)MemberwiseClone();
            cpRect.pt = cpRect.pt.Clone() as Point;

            return cpRect;
        }

        public override string ToString()
        {
            return $"Rect:[{width}, {height}, pt:{pt}]";
        }
    }
    class Program
    {
        private static void Main(string[] args)
        {
            Rect r1 = new Rect(new Point(2, 3), 10, 20);
            Rect r2 = r1.Clone() as Rect;
            r2.Width = 100;
            r2.PT.X = 50;

            Console.WriteLine(r1);
            Console.WriteLine(r2);
        }
    }
}

