using System;
using System.Collections;
using System.ComponentModel;
using System.Runtime.InteropServices;

namespace ex251103
{
    class Point : ICloneable, IComparable
    {
        private int x;
        public int X { get { return x; } }
        private int y;
        public int Y => y;

        public Point(int x, int y)
        {
            /* 빈칸 */
            this.y = y;
        }
        public object Clone()
        {
            return MemberwiseClone() as Point;
        }

        public int CompareTo(object obj)
        {
            Point pt1 = this;
            Point pt2 = obj as Point;

            if (pt1.y > pt2.y)
                return 1;
            else if (pt1.y < pt2.y)
                return -1;
            else
                return pt1.x - pt2.x;
        }
        public override string ToString()
        {
            return $"({x},{y})";
        }
    }
    class Program
    {
        static void Main(string[] args)
        {
            Point[] parr = {
                new Point(4,5),
                new Point(3,5),
                new Point(1,2),
                new Point(7,3),
                new Point(6,2),
            };

            Array.Sort(parr);

            foreach(var item in parr)
                Console.WriteLine(item);
        }
    }
}

