using System;
namespace ex251117
{
    delegate void DelPrint(string msg, int data);

    internal class Program
    {
        static void Print(string msg, int data)
        {
            Console.WriteLine("Message : {0}, data : {1}", msg, data);
        }
        static void PrintLoop(string msg, int count)
        {
            for (int i = 0; i < count; ++i)
                Console.WriteLine("{0} : {1}", i + 1, msg);
        }

        static void Main(string[] args)
        {
            /* 빈칸 */
            PrintLoop("Hello", 10);
            DelPrint d1 = Print;
            d1("Hello", 10);
            d1 = PrintLoop;
            d1("Hello", 10);
        }
    }
}

