using System;
using System.Timers;

namespace ex251124
{
    class IndexRandomArgs : EventArgs
    {
        public int Index { get; set; }
    }
    class ArrayPrinter
    {
        public event EventHandler<IndexRandomArgs> IndexEvent;

        private int[] buf;
        public ArrayPrinter(int[] arr)
        {
            /* 빈칸 */
        }
        public void Print()
        {
            int rIndex = new Random().Next(buf.Length);
            for (int i = 0; i < buf.Length; ++i)
            {
                if (i == rIndex)
                    IndexEvent?.Invoke(this, new IndexRandomArgs() { Index = i });
                else
                    Console.WriteLine($"[{i}] : {buf[i]}");
            }
        }
    }
    internal class Program
    {
        static void Main(string[] args)
        {
            ArrayPrinter ap = new ArrayPrinter(new[] { 6, 5, 90, 76, 35, 76, 23 });
            ap.IndexEvent += (sender, e) =>
            {
                Console.WriteLine("{0} index 에서 랜덤 이벤트 발생!", e.Index);
            };
            ap.Print();
        }
    }
}

