namespace ex251201
{
    class NotIntegerException : ApplicationException
    {
        string errorMessage;
        public NotIntegerException(string msg, string em) : base(msg) { }

        public override string Message => base.Message + " : " + errorMessage;

    }
    static class Integer
    {
        public static int InputInteger()
        {
            Console.Write("정수를 입력 : ");
            /* 빈칸 */
            if (int.TryParse(data, out int value))
            {
                return value;
            }
            else
            {
                throw new NotIntegerException("NotIntegerExecption 발생 ", data);
            }
        }
        public static void PrintInteger(int value)
        {
            Console.WriteLine($"integer : {value}");
        }
    }
    internal class Program
    {
        static void Main(string[] args)
        {
            try
            {
                int data = Integer.InputInteger();
                Integer.PrintInteger(data);
            }
            catch (NotIntegerException e)
            {
                Console.WriteLine("NotIE + "+ e.Message );
            }
            catch (Exception e)
            {
                Console.WriteLine("예외! : {0}", e.Message);
                Console.WriteLine("예외! : {0}", e.StackTrace);
            }

            Console.WriteLine("독립적인 작업 : 프로그램 완료~");
        }
    }
}

