template:
using System;
using System.Threading;

namespace ex251201
{
    static class ThreadInfo
    {
        public static int ID => Thread.CurrentThread.ManagedThreadId;
    }
    class Program
    {
        static void PrintHello()
        {
            for (int i = 0; i < 10; ++i)
            {
                Console.WriteLine($"[{ThreadInfo.ID} - {i} : Hello");
                /* 빈칸 */
            }
        }
        static void Main(string[] args)
        {
            Thread t1 = new Thread(new ThreadStart(PrintHello));
            Thread t2 = new Thread(new ThreadStart(PrintHello));
            t1.Start();
            t2.Start();

            Console.WriteLine($"[{ThreadInfo.ID}]는 대기중...");
            Console.ReadLine();
            Console.WriteLine(" 프로그램 종료 !");
        }
    }
}

