using System;
using System.Threading;
using System.Threading.Tasks;
namespace ex251208
{
    internal class Program
    {
        static int TID => Thread.CurrentThread.ManagedThreadId;
        static async Task a()
        {
            Console.WriteLine("시작 a() : {0}", TID);
            /* 빈칸 */
            await Task.Delay(1000);
            Console.WriteLine("a()");
            Console.WriteLine("끝 a() : {0}", TID);
        }
        static async Task<double> b()
        {
            Random rnd = new Random();
            await Task.Delay(rnd.Next(1000, 5000));
            Console.WriteLine("b()");
            return 5.123;
        }
        static async Task c()
        {
            Random rnd = new Random();
            await Task.Delay(rnd.Next(1000, 5000));
            Console.WriteLine("c()");
        }
        static void Main(string[] args)
        {
            Console.WriteLine("시작 Main() : {0}", TID);
            a();
            Console.WriteLine("실행 Main() : {0}", TID);
            Console.ReadLine();
        }
    }
}

